   $direct_values = $direct_values.'"'.$data.'"';              array_push($excepted_attrs,$labels[$value_index]);             if($j < count($direct_to_db_folder)-1) {                 $direct_attributes = $direct_attributes.",";                 $direct_values = $direct_values.",";             }         }         $query = "INSERT INTO ".$table_name."(".$direct_attributes.") VALUES(".$direct_values.")";         $sql = $conn->prepare($query);         $sql->execute();         $lastInsertId = $conn->lastInsertId();          $query ="SHOW KEYS FROM ".$table_name." WHERE Key_name = 'PRIMARY'";         $sql = $conn->prepare($query);         $sql->execute();         $result = $sql->fetchAll();         foreach( $result as $baris ) {             $primary_column = $baris["Column_name"];          }         $attr = array($primary_column => $lastInsertId);          $row = $row + $attr;          $json_name = "update.json";         $i = 0;         foreach($values as $value){             $is_value = true;             foreach($excepted_attrs as $excepted_attr){                 if($labels[$i] == $excepted_attr) $is_value = false;             }             if($is_value){                 if(is_array($value)) $attr = array($labels[$keys[$i]] => implode(", ",$value));                  else $attr = array($labels[$keys[$i]] => $value);                 $row = $row + $attr;             }         $i++;         }     }     else{         $json_name = "insert.json";        $i = 0;        foreach($labels as $value){            if(is_array($value)){                $attr = array($keys[$i] => implode(", ",$value));            }else $attr = array($keys[$i] => $value);            $row = $row + $attr; $i++;        }        $myJSON = json_encode($row);        $fp = fopen("files/".$json_name, "w");        fwrite($fp, $myJSON);        fclose($fp);        $namaFoldSync = "";        $optParams = array(            'pageSize' => 1,            'orderBy' => 'modifiedTime asc',            'fields' => 'nextPageToken